import React, { useState } from 'react';

export default function App() {
  const [clickArray, setClickArray] = useState([]);

  return (
    <>
      <h1>
        React com <em>Hooks</em>
      </h1>

      <button onClick={this.handleClick}>Clique aqui</button>

      <ul>
        {clickArray.map((item) => {
          return <li key={item}>{item}</li>;
        })}
      </ul>
    </>
  );
}
